var win = Titanium.UI.createWindow({
  backgroundColor: "#FFFFFF" });


const AdMob = require('ti.admob');

AdMob.initialize({ 'appId': 'ca-app-pub-3940256099942544~3347511713' });

const bannerView = AdMob.createBannerView({
  adUnitId: 'ca-app-pub-3940256099942544/2934735716',
  adSize: AdMob.AD_SIZE_SMART_BANNER });


bannerView.load();
win.add(bannerView);


var showInterstitialAdButton = Ti.UI.createButton({
  title: "Show Interstitial",
  top: "40%",
  height: "10%",
  width: "80%" });


const interstitial = AdMob.createInterstitialAd({
  adUnitId: 'ca-app-pub-3940256099942544/4411468910' });

interstitial.load();

showInterstitialAdButton.addEventListener('click', function () {
  if (interstitial.isReady) {
    interstitial.show();
  }
});

win.add(showInterstitialAdButton);

win.open();
